mapper.select:
select m_id, title, to_char(w_date, 'YYYY-MM-DD') as "w_date",
imagename
from (
select rownum rn, a.*
from (
select m.m_id, m.title,
m.w_date, m.imagename
from meetup m
order by m.m_id desc
) a
)
where rn >
#{start} and rn <
#{end}

mapper.update:
update meetup set title=#{title}, info=#{info},
c_key=#{c_key},
price=#{price}, w_date=#{w_date},
imagename=#{imagename}
where m_id=#{m_id}
